


import requests

api_url = "http://127.0.0.1:8000/trip/invoke"
payload = {
    "input": {
        "input": "Trip Planning for this ChaungTharBeach with family within 5/15/2025 to 5/20/2025 with 1000000 MMK"
    }
}

try:
    response = requests.post(api_url, json=payload, timeout=5)
    print(response.json())
except requests.exceptions.RequestException as e:
    print("Request failed:", e)
